'use strict';

const setRequestPayload = (url, ctx, payload) => payload ? {
  url: url,
  headers: _setHeaders(ctx.state.logger.fields.req_id, ctx.state.logger.fields.session_id),
  body: payload
} : {
  url: url,
  headers: _setHeaders(ctx.state.logger.fields.req_id, ctx.state.logger.fields.session_id)
};

const _setHeaders = (reqId, sessionId) => ({
  'X-Request-Id': reqId,
  'X-Session-Id': sessionId
});

module.exports = {
  setRequestPayload: setRequestPayload
};
