'use strict';

let dataClient;

function * getCategorySlugs (ctx) {
  let result = yield dataClient.get('/getCategorySlugs/', ctx);
  return result.body;
}

function * getEventSlugs (ctx) {
  let result = yield dataClient.get('/getEventSlugs/', ctx);
  return result.body;
}

function * getPageSlugs (ctx) {
  let result = yield dataClient.get('/getPageSlugs/', ctx);
  return result.body;
}

module.exports = function init (config) {
  dataClient = require('../utils/rest-client')(config.data_api, false);
  return {
    getCategorySlugs: getCategorySlugs,
    getEventSlugs: getEventSlugs,
    getPageSlugs: getPageSlugs
  };
};
