'use strict';

const Joi = require('joi');

const finishLogin = Joi.object().keys({
  code: Joi.string().optional(),
  state: Joi.string().max(256)
}).unknown(true);

const initiateLogin = Joi.object().keys({
  redirect_uri: Joi.string().optional()
}).unknown(true);

const sendPayment = Joi.object().keys({
  accessToken: Joi.string().required(),
  senderPin: Joi.string().required(),
  destinationId: Joi.string().required(),
  amount: Joi.number().integer().positive().max(9999999999).required().description('Currency amount in the lowest subdivision of the currency, e.g. cents for USD')
});

module.exports = {
  finishLogin: finishLogin,
  sendPayment: sendPayment,
  initiateLogin: initiateLogin
};
