'use strict';

const Joi = require('joi');
const common = require('../common');

// INPUT VALIDATIONS

const getPolicyById = Joi.object().keys({
  id: common.id.required()
});

const getPolicies = Joi.object().keys({
  ids: Joi.array().items(common.id)
});

module.exports = {
  getPolicyById: getPolicyById,
  getPolicies: getPolicies
};
