'use strict';

var Joi = require('joi');

var searchQuery = Joi.object().keys({
  collection: Joi.string().valid('merchant', 'event', 'category', 'deal').insensitive(),
  query: Joi.string().insensitive().required(),
  start: Joi.number().min(0),
  limit: Joi.number().positive().max(25),
  region: Joi.string().max(2),
  lang: Joi.string().max(2)
}).unknown(true);

module.exports = {
  searchQuery: searchQuery
};
