'use strict';

var Joi = require('joi');
const common = require('../common');

var getDefaultMerchantRate = Joi.object().keys({
  userId: common.id.required(),
  merchantId: common.id.required()
});

var getCashbackParams = Joi.object().keys({
  userId: common.id.required(),
  merchantId: common.id.required(),
  rateId: common.id.required()
});

var getDealParams = Joi.object().keys({
  userId: common.id.required(),
  merchantId: common.id.required(),
  dealId: common.id.required()
});

var allowUserIdInQuery = Joi.object().keys({
  id: common.id.description('ID of the user who should be associated with any purchase tracking -- an optional query param if headers or cookies are not available')
}).unknown(true);

module.exports = {
  getDefaultMerchantRate: getDefaultMerchantRate,
  getCashbackParams: getCashbackParams,
  getDealParams: getDealParams,
  allowUserIdInQuery: allowUserIdInQuery
};
