'use strict';

var Joi = require('joi');
const common = require('../common');

var incomingGateways = Joi.object().keys({
  gateway: common.gateway.insensitive().required()
});

var webgainsCommissionWebhook = Joi.object().keys({
  id: Joi.number().integer().min(0).required(),
  clickReference: Joi.string().required(),
  value: Joi.number().required(),
  commission: Joi.number().required(),
  currency: Joi.string().required(),
  state: Joi.string().valid('confirmed', 'cancelled', 'delayed').required()
}).unknown(true);

var partneradsCommissionWebhook = Joi.object().keys({
  uid: Joi.string().required(),
  ordre: Joi.string().required(),
  prov: Joi.number().required(),
  sum: Joi.number().required(),
  prg: Joi.string().required()
}).unknown(true);

module.exports = {
  incomingGateways: incomingGateways,
  webgainsCommissionWebhook: webgainsCommissionWebhook,
  partneradsCommissionWebhook: partneradsCommissionWebhook
};
