'use strict';

var uuid = require('node-uuid');

function generateCode (isMobile) {
  var code = '';
  if (isMobile) {
    var rnum = (1000000 + (Math.random() * 100000)|0);
    code = rnum.toString().slice(1).replace(/^(\d{3})/, '$1 ');
  } else {
    code = uuid.v4();
  }
  return code;
}

module.exports = generateCode;
