'use strict';

var createHash = require('crypto').createHash;

function hash (item) {
  var s_string = item;
  if (typeof item === 'object') {
    var values = [];
    Object.keys(item).sort().forEach(function (key) {
      values.push(key + '=' + item[key]);
    });
    s_string = values.join('&');
  }
  return createHash('md5').update(s_string).digest('hex');
}

module.exports = hash;
