'use strict';

const chai = require('chai');
const expect = chai.expect;

const duck = require('../../src/utils/duck-checks');

describe('duck-check', () => {

  it('should be available as an object', () => {
    expect(duck).to.be.a('object');
  });

  describe('amountCheck', () => {

    [1, 1.002].forEach(value => {
      it('returns true for ' + value, () => {
        expect(duck.amountCheck(value)).to.equal(true);
      });
    });

    ['1', -1, () => {
    }, null, undefined].forEach(value => {
        it('returns false for ' + value, () => {
          expect(duck.amountCheck(value)).to.equal(false);
        });
      });
  });
});
