"use strict";

describe('date-format', function () {
  const expect = require('chai').expect;
  const generateCode = require('../../src/utils/generate-activation-code.js');
  const expectedToMatch = /^\d{3}\s\d{3}$/g;

  describe('generateCode', function () {
    it('is available as a function', function () {
      expect(generateCode).to.be.an('function');
    });

    it('generates a short code if mobile parameter is true', function () {
      var actual = generateCode(true);
      expect(actual).to.match(expectedToMatch);
    });
  });
});
