'use strict';

var getDataClient = require(__basedir + "src/utils/get-data-client");

describe('get-data-client', function () {

  it('should not throw an error if using defaults many times', function () {
    var client = getDataClient();
    for (var i = 0; i < 100; i++) {
      client = client.defaults({});
    }
  });
});
