"use strict";

describe('utils/mustache-simple', function () {
  var expect = require('chai').expect;
  var util = require('../../src/utils/mustache-simple');
  var replace = util.replace;

  var template = 'The {{speed}} {{color}} fox jumped over the lazy {{mammal}}';
  var data = {speed: "quick", color: "brown", mammal: "dog"};
  var final = 'The quick brown fox jumped over the lazy dog';

  describe('replace', function () {
    it('is available as a function', function () {
      expect(replace).to.be.a('function');
    });

    it('should properly replace mustached parameters', function () {
      expect(replace(template, data)).to.eql(final);
    });
  });

});
